commits.content:
body {
    background-image: url('../images/outlinehills.jpg');
    background-size: cover;
    background-repeat: no-repeat;
    color: #fff;
    /* filter: grayscale(10); */
}

.login-container {
    padding: 40px;
    margin: 100px auto;
    width: 40%;
    height: 70%;
    border-radius: 10px;
    background-color: #f39b9b;
    box-shadow: 0px 0px 15px 2px rgba(140, 153, 228, 0.856);
    position: relative;
    z-index: 1;

    justify-content: center;
    align-items: center;
    text-align: center;
    
    background-image: url('../photos/doodle.jpg');
    background-size: cover;
    background-repeat: no-repeat;
    /* overflow: hidden; */
    transform: translateY(20%);
    
    /* position: relative; */
}

.loginform h1 {
    text-align: center;
    color: #ffffff;
    margin-bottom: 20px;
    
    text-shadow: 0px 4px 14px rgba(150, 159, 236, 0.815);
}

.login-container input[type="text"],
.login-container input[type="password"] {
    width: 70%;
    padding: 10px;
    margin: 20px;
    border: none;
    border-bottom: 3px solid rgb(199, 243, 4);
    border-radius: 5px;
    outline: none;
}

.login-container input[type="submit"] {
    width: 30%;
    padding: 10px;
    border: none;
    background-color: #32db5ccb;
    color: #fff;
    cursor: pointer;
    border-radius: 5px;
}

.login-container input[type="submit"]:hover {
    background-color: #09f528ce;
    color: rgb(5, 5, 5);
    font-weight: bolder;
}

.login-container a {
    color: #b7dd0e;
    text-decoration: none;
    display: inline-block; 
    transition: transform 0.3s ease;
}

.login-container a:hover {
    color: #fa0606;
    transform: scale(1.1);
    background-color: #f0eff1;
    border-radius: 5px;
    font-weight: bold;
}


.avatar_profile {
    position: absolute;
    top: -10px; /* Adjust this value to position the avatar */
    left: 50%;
    transform: translateX(-50%);
    color: whitesmoke;
    text-shadow: 0px 4px 14px rgba(150, 159, 236, 0.815);
    font-size: 3em; /* Adjust the size of the avatar */
    z-index: 2; /* Ensure the avatar is above other content */
}

/* Register css start */
